<?php	define ( "IN_VIEWIT", 1 );		// Display Name, area, file Show in menu	$Elements = array(		array( "Home", "home", "homepage", 1 ),		array( "Register", "register", "register", 1 ),		array( "Upload", "upload", "upload", 1 ),		array( "Watch", "watch", "watch", 1 ),		array( "Profile", "profile", "profile", 0 ),		array( "Forgot Password", "forgot", "forgot", 0 ),		array( "Admin", "admin", "admin", 0 ),		array( "Report", "report", "report", 0 ),		array( "Edit", "editvideo", "edit", 0 ),		array( "Comment Report", "commentreport", "commentreport", 0 ),	);		$DefaultArea = 0;		require_once( "includes/global.php" );		if( $DB->wasError() )	{		echo "<br /><div class='error'>Critical! Database connection error!</div><br />";	}else{			$area = ( ( empty( $_GET['area'] ) )?( $DefaultArea ):( $_GET['area'] ) );		$located = 0;				foreach( $Elements as $Element )		{			if( $Element[1] == $area and !empty( $Element[2] ) )			{				$located = 1;				require_once( "pages/" . $Element[2] . ".php" );				break;			}		}			if( $located != 1 || $_GET['area'] == "" ){ require_once( "pages/" . $Elements[$DefaultArea][2] . ".php" ); }		}		require_once( "includes/footer.php" );	?>