<?php	if( !defined( "IN_VIEWIT" ) )	{		die("Access deined!");	}		$Comment = $_GET['cid'];		if( $User->isGuest() )	{		$User->setMessage( "You must be logged in to use that feature!" );	}else{		if( is_numeric( $Comment ) && !empty( $Comment ) )		{			$resp = recaptcha_check_answer( $PrivateKey, $_SERVER["REMOTE_ADDR"], $_GET["recaptcha_challenge_field"], $_GET["recaptcha_response_field"] );						if (!$resp->is_valid) {				$User->setMessage( "You're not human?!?" );								$_GET['area'] = "commentreport";				$_GET['cid'] = $Comment;			}elseif( strlen( $Reason ) <= 50 ){				Video::doCommentReport($Comment);				$User->setMessage( "That comment has been reported!", 2 );			}		}	}?>