<?php	if( !defined( "IN_VIEWIT" ) )	{		die("Access deined!");	}		if( $User->isGuest() || !( $User->isAdmin() ) )	{		$User->setMessage( "Access Denied" );	}else{		echo "			<h2>Video Reports</h2>			<table class='reportsTable' cellspacing='0'>				<thead>					<tr>						<td style='width: 15%;text-align: center;'>ID</td>						<td style='width: 25;%'>User</td>						<td style='width: 50%;'>Reason</td>						<td style='width: 15%;'>Video</td>					</tr>				</thead>								<tbody>		";				$Reports = $DB->query( "SELECT video_reports.*, users.username FROM video_reports, users WHERE users.ID = video_reports.UserID;" );		$ReportAmount = 0;				while( $report = $DB->get_arr( $Reports ) )		{			$ReportAmount++;						echo "				<tr ".(($ReportAmount % 2 == 1)?("class='reportOdd'"):(""))." >					<td style='text-align: center;'>".$report['ID']."</td>					<td>".$report['username']."</td>					<td>".$report['Reason']."</td>					<td><a href='?area=watch&id=".$report['VideoID']."'>".$report['VideoID']."</a></td>				</tr>			";		}				echo "				</tbody>			</table>		";				echo "			<br />			<h2>Comment Reports</h2>			<table class='reportsTable' cellspacing='0'>				<thead>					<tr>						<td style='width: 15%;text-align: center;'>ID</td>						<td style='width: 25;%'>User</td>						<td style='width: 50%;'>Comment</td>						<td style='width: 10%;'>Admin</td>					</tr>				</thead>								<tbody>		";				$Reports = $DB->query( "SELECT comment_reports.*, comments.Comment, users.username FROM comment_reports, users, comments WHERE users.ID = comments.UserID AND comments.ID = comment_reports.CommentID;" );		$ReportAmount = 0;				while( $report = $DB->get_arr( $Reports ) )		{			$ReportAmount++;						echo "				<tr ".(($ReportAmount % 2 == 1)?("class='reportOdd'"):(""))." >					<td style='text-align: center;'>".$report['ID']."</td>					<td>".$report['username']."</td>					<td>".$report['Comment']."</td>					<td><a href='?area=deletecomment&cid=".$report['CommentID']."' title='Remove Comment'>RC</a> - <a href='?area=deletecommentreports&cid=".$report['CommentID']."' title='Remove Report'>RR</a></td>				</tr>			";		}				echo "				</tbody>			</table>		";	}	?>